<script>
import { Link, router } from "@inertiajs/vue3";
import { TabsService } from "../services/TabsService";

export default {
    components: {
        Link,
    },
    created() {
        window.addEventListener("resize", this.handleResize);
        this.handleResize();
    },
    data() {
        return {
            viewportWidth: 0,
            service: new TabsService(),
        };
    },
    methods: {
        back() {
            router.visit("/profile/statistic");
            // if (window.history.length > 1) {
            //     window.history.back();
            // } else {
            //     router.visit("/profile/statistic");
            // }
        },
        router() {
            return router;
        },
        handleResize() {
            this.viewportWidth = window.innerWidth;
        },
    },
    mounted() {
        this.service.setLinks();
    },
    computed: {
        fullPage() {
            let url = this.$page.url.startsWith("http")
                ? new URL(this.$page.url).pathname
                : this.$page.url;
            return url.startsWith("/profile/full-page");
        },
        links() {
            return this.service.links;
        },
    },
};
</script>
